import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './LandingPage.css';
import LoadingScreen from './LoadingScreen';

const LandingPage = () => {
  const [shops, setShops] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('all');
  const [isLoading, setIsLoading] = useState(true);
  const [showContent, setShowContent] = useState(false);
  const [showPhoneNumbers, setShowPhoneNumbers] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const fetchShops = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/shops');
        setShops(response.data);
      } catch (error) {
        console.error('Error fetching shops:', error);
      }
    };

    fetchShops();
  }, []);

  useEffect(() => {
    if (!isLoading) {
      const timer = setTimeout(() => {
        setShowContent(true);
      }, 500);
      return () => clearTimeout(timer);
    }
  }, [isLoading]);

  const filteredShops = shops.filter(shop => {
    const searchLower = searchTerm.toLowerCase();
    if (searchType === 'all') {
      return (
        shop.shopName.toLowerCase().includes(searchLower) ||
        shop.location.toLowerCase().includes(searchLower) ||
        shop.ownerName.toLowerCase().includes(searchLower)
      );
    } else if (searchType === 'name') {
      return shop.shopName.toLowerCase().includes(searchLower);
    } else if (searchType === 'location') {
      return shop.location.toLowerCase().includes(searchLower);
    } else if (searchType === 'owner') {
      return shop.ownerName.toLowerCase().includes(searchLower);
    }
    return true;
  });

  const togglePhoneNumber = (shopId) => {
    setShowPhoneNumbers(prev => ({
      ...prev,
      [shopId]: !prev[shopId]
    }));
  };

  const handleToggleStatus = async (shopId) => {
    try {
      const shop = shops.find(s => s._id === shopId);
      const newStatus = !shop.isOpen;

      await axios.put(`http://localhost:5000/api/shops/${shopId}/toggle-status`, {
        isOpen: newStatus
      });

      setShops(prevShops =>
        prevShops.map(shop =>
          shop._id === shopId
            ? { ...shop, isOpen: newStatus }
            : shop
        )
      );
    } catch (error) {
      console.error('Error toggling shop status:', error);
    }
  };

  const clearSearch = () => {
    setSearchTerm('');
    setSearchType('all');
  };

  const isAuthenticated = localStorage.getItem('token');
  const isOwner = (shop) => {
    const userId = localStorage.getItem('userId');
    return shop.ownerId === userId;
  };

  const getImageUrl = (imageUrl) => {
    if (!imageUrl) return 'https://via.placeholder.com/300x200?text=No+Image';
    return imageUrl.startsWith('http') ? imageUrl : `http://localhost:5000${imageUrl}`;
  };

  return (
    <>
      {isLoading && <LoadingScreen onAnimationComplete={() => setIsLoading(false)} />}
      <div className={`landing-page ${!isLoading ? 'content-visible' : ''}`}>
        <header className="header">
          <div className="header-left">
            <img 
              src="https://res.cloudinary.com/diqqos08s/image/upload/v1743523532/Screenshot_21_o5te9p.png" 
              alt="Vendor Availability Check" 
              className="header-logo"
            />
            <h1 className="header-title">Vendor Availability Check</h1>
          </div>
          <div className="header-right">
            {isAuthenticated ? (
              <div className="auth-buttons">
                <Link to="/add-shop" className="add-shop-btn">
                  Add Shop
                </Link>
                <Link to="/profile" className="profile-btn">
                  Profile
                </Link>
                <button 
                  className="logout-btn"
                  onClick={() => {
                    localStorage.removeItem('token');
                    localStorage.removeItem('userId');
                    navigate('/');
                  }}
                >
                  Logout
                </button>
              </div>
            ) : (
              <div className="auth-buttons">
                <Link to="/login" className="login-btn">
                  Login
                </Link>
                <Link to="/register" className="register-btn">
                  Register
                </Link>
              </div>
            )}
          </div>
        </header>

        <div className="search-section">
          <div className="search-controls">
            <select
              value={searchType}
              onChange={(e) => setSearchType(e.target.value)}
              className="search-select"
            >
              <option value="all">All Fields</option>
              <option value="name">Shop Name</option>
              <option value="location">Location</option>
              <option value="owner">Owner Name</option>
            </select>
            {searchTerm && (
              <button onClick={clearSearch} className="clear-search">
                Clear Search
              </button>
            )}
          </div>
          <input
            type="text"
            placeholder={`Search by ${searchType === 'all' ? 'any field' : searchType}...`}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <div className="search-results-count">
            {filteredShops.length} shops found
          </div>
        </div>

        <div className="shops-container">
          {filteredShops.length === 0 ? (
            <div className="no-results">
              No shops found matching your search criteria
            </div>
          ) : (
            filteredShops.map((shop) => (
              <div key={shop._id} className="shop-card">
                <img 
                  src={getImageUrl(shop.imageUrl)} 
                  alt={shop.shopName} 
                  className="shop-image"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/300x200?text=Error+Loading+Image';
                  }}
                />
                <div className="shop-info">
                  <h2>{shop.shopName}</h2>
                  <p>Owner: {shop.ownerName}</p>
                  <p>Location: {shop.location}</p>
                  <div className="phone-number-section">
                    <button 
                      className="view-phone-btn"
                      onClick={() => togglePhoneNumber(shop._id)}
                    >
                      {showPhoneNumbers[shop._id] ? 'Hide Phone' : 'View Phone'}
                    </button>
                    {showPhoneNumbers[shop._id] && (
                      <p className="phone-number">Phone: {shop.phoneNumber}</p>
                    )}
                  </div>
                  <p className={`status ${shop.isOpen ? 'open' : 'closed'}`}>
                    {shop.isOpen ? 'Open' : 'Closed'}
                  </p>
                  {isAuthenticated && isOwner(shop) && (
                    <div className="shop-actions">
                      <Link to={`/edit-shop/${shop._id}`} className="edit-btn">
                        Edit Shop
                      </Link>
                      <button
                        className="toggle-status-btn"
                        onClick={() => handleToggleStatus(shop._id)}
                      >
                        {shop.isOpen ? 'Mark as Closed' : 'Mark as Open'}
                      </button>
                    </div>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </>
  );
};

export default LandingPage;
