import React, { useState, useEffect } from 'react';
import './LoadingScreen.css';

const LoadingScreen = ({ onAnimationComplete }) => {
  const [position, setPosition] = useState('centered');
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    // Start with the logo in the center
    setTimeout(() => {
      // Move logo to top-left corner
      setPosition('top-left');
      
      // After the animation completes, trigger the fade out
      setTimeout(() => {
        setIsVisible(false);
        // Notify parent component that animation is complete
        if (onAnimationComplete) {
          setTimeout(onAnimationComplete, 500);
        }
      }, 2000); // Match this with the CSS transition duration
    }, 2000); // Initial delay before starting the journey
  }, [onAnimationComplete]);

  return (
    <div className={`loading-screen ${!isVisible ? 'fade-out' : ''}`}>
      <div className={`loading-logo-container ${position}`}>
        <img 
          src="https://res.cloudinary.com/diqqos08s/image/upload/v1743523532/Screenshot_21_o5te9p.png"
          alt="Vendor Availability Logo"
          className="loading-logo"
        />
      </div>
    </div>
  );
};

export default LoadingScreen;
