import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PaymentButton = ({ amount, shopId, onSuccess }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Load Razorpay script
  useEffect(() => {
    const loadRazorpay = () => {
      return new Promise((resolve) => {
        const script = document.createElement('script');
        script.src = 'https://checkout.razorpay.com/v1/checkout.js';
        script.onload = () => resolve(true);
        script.onerror = () => resolve(false);
        document.body.appendChild(script);
      });
    };

    loadRazorpay();
  }, []);

  const handlePayment = async () => {
    try {
      setLoading(true);
      setError(null);

      // Create order
      const orderResponse = await axios.post('http://localhost:5000/api/payment/create-order', {
        amount,
        shopId
      });

      if (!orderResponse.data.success || !orderResponse.data.order) {
        throw new Error(orderResponse.data.error || 'Failed to create order');
      }

      const options = {
        key: process.env.REACT_APP_RAZORPAY_KEY_ID,
        amount: orderResponse.data.order.amount,
        currency: orderResponse.data.order.currency,
        name: "Vendor Availability Check",
        description: "Payment for viewing vendor contact",
        order_id: orderResponse.data.order.id,
        handler: async (response) => {
          try {
            // Verify payment
            const verifyResponse = await axios.post('http://localhost:5000/api/payment/verify', {
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature
            });

            if (verifyResponse.data.success) {
              onSuccess(verifyResponse.data.payment);
            } else {
              setError(verifyResponse.data.message || 'Payment verification failed');
            }
          } catch (error) {
            console.error('Payment verification error:', error);
            setError(error.response?.data?.error || 'Payment verification failed');
          }
        },
        prefill: {
          name: "User Name",
          email: "user@example.com",
          contact: "9999999999"
        },
        notes: {
          shopId: shopId
        },
        theme: {
          color: "#528FF0"
        }
      };

      if (!window.Razorpay) {
        throw new Error('Razorpay SDK not loaded');
      }

      const razorpay = new window.Razorpay(options);
      razorpay.open();

      razorpay.on('payment.failed', async (response) => {
        try {
          // Log failed payment
          await axios.post('http://localhost:5000/api/payment/failed', {
            razorpay_order_id: response.error.metadata.order_id,
            error_code: response.error.code,
            error_description: response.error.description
          });
          setError(response.error.description || 'Payment failed. Please try again.');
        } catch (error) {
          console.error('Error logging payment failure:', error);
          setError('Payment failed. Please try again.');
        }
      });

    } catch (error) {
      console.error('Payment error:', error);
      setError(error.response?.data?.error || error.message || 'Failed to initiate payment. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="payment-button-container">
      {error && <div className="payment-error">{error}</div>}
      <button
        className="payment-button"
        onClick={handlePayment}
        disabled={loading}
      >
        {loading ? 'Processing...' : `Pay â‚¹${amount}`}
      </button>
    </div>
  );
};

export default PaymentButton;
