import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './LandingPage.css';
import LoadingScreen from './LoadingScreen';

const LandingPage = () => {
  const [shops, setShops] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredShops, setFilteredShops] = useState([]);
  const [searchBy, setSearchBy] = useState('all'); // 'all', 'location', 'shopName'
  const [statusFilter, setStatusFilter] = useState('all'); // 'all', 'open', 'closed'
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [showContent, setShowContent] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);

    // Longer initial delay to show the centered logo
    setTimeout(() => {
      setIsLoading(false);
      // Longer delay before showing content for a more visible animation
      setTimeout(() => {
        setShowContent(true);
      }, 1000); // Increased from 500ms to 1000ms
    }, 2500); // Increased from 2000ms to 2500ms
  }, []);

  useEffect(() => {
    const fetchShops = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/shops');
        setShops(response.data);
        setFilteredShops(response.data);
      } catch (error) {
        console.error('Error fetching shops:', error);
      }
    };

    fetchShops();
  }, []);

  useEffect(() => {
    let filtered = [...shops];

    // Apply search term filter
    if (searchTerm.trim()) {
      filtered = filtered.filter(shop => {
        const searchLower = searchTerm.toLowerCase();
        switch (searchBy) {
          case 'location':
            return shop.location.toLowerCase().includes(searchLower);
          case 'shopName':
            return shop.shopName.toLowerCase().includes(searchLower);
          default: // 'all'
            return (
              shop.location.toLowerCase().includes(searchLower) ||
              shop.shopName.toLowerCase().includes(searchLower) ||
              shop.ownerName.toLowerCase().includes(searchLower)
            );
        }
      });
    }

    // Apply status filter
    if (statusFilter !== 'all') {
      filtered = filtered.filter(shop => 
        shop.isOpen === (statusFilter === 'open')
      );
    }

    setFilteredShops(filtered);
  }, [searchTerm, searchBy, statusFilter, shops]);

  const getImageUrl = (imageUrl) => {
    if (!imageUrl) return 'https://via.placeholder.com/300x200?text=No+Image';
    return imageUrl.startsWith('http') ? imageUrl : `http://localhost:5000${imageUrl}`;
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    window.location.reload();
  };

  const handleToggleStatus = async (shopId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        return;
      }

      await axios.patch(
        `http://localhost:5000/api/shops/${shopId}/toggle-status`,
        {},
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );

      // Update the shops list
      const updatedShops = shops.map(shop => {
        if (shop._id === shopId) {
          return { ...shop, isOpen: !shop.isOpen };
        }
        return shop;
      });
      setShops(updatedShops);
      setFilteredShops(updatedShops);
    } catch (error) {
      console.error('Error toggling shop status:', error);
    }
  };

  const isOwner = (shop) => {
    const user = JSON.parse(localStorage.getItem('user'));
    return user && shop.ownerId === user.id;
  };

  if (isLoading) {
    return <LoadingScreen />;
  }

  return (
    <div className={`landing-page ${showContent ? 'content-visible' : ''}`}>
      <header className="header">
        <div className={`header-logo ${showContent ? 'logo-positioned' : ''}`}>
          <img 
            src="https://res.cloudinary.com/diqqos08s/image/upload/v1743523532/Screenshot_21_o5te9p.png" 
            alt="Vendor Availability Logo"
            className="logo-image"
          />
        </div>
        <h1 className="header-title">Vendor Availability Check</h1>
        <div className="header-buttons">
          {isAuthenticated ? (
            <>
              <Link to="/add-shop" className="add-shop-btn">
                Add Shop
              </Link>
              <button onClick={handleLogout} className="logout-btn">
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login" className="login-btn">
                Login
              </Link>
              <Link to="/register" className="register-btn">
                Register
              </Link>
            </>
          )}
        </div>
      </header>

      <div className="search-container">
        <div className="search-controls">
          <select 
            value={searchBy} 
            onChange={(e) => setSearchBy(e.target.value)}
            className="search-select"
          >
            <option value="all">Search All</option>
            <option value="location">Search by Location</option>
            <option value="shopName">Search by Shop Name</option>
          </select>

          <select 
            value={statusFilter} 
            onChange={(e) => setStatusFilter(e.target.value)}
            className="search-select"
          >
            <option value="all">All Status</option>
            <option value="open">Open Only</option>
            <option value="closed">Closed Only</option>
          </select>
        </div>

        <input
          type="text"
          placeholder={
            searchBy === 'location' 
              ? "Search by location..." 
              : searchBy === 'shopName'
              ? "Search by shop name..."
              : "Search by location, shop name, or owner..."
          }
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />

        <div className="search-results-count">
          {filteredShops.length} shops found
          {searchTerm && (
            <button 
              className="clear-search"
              onClick={() => {
                setSearchTerm('');
                setSearchBy('all');
                setStatusFilter('all');
              }}
            >
              Clear Search
            </button>
          )}
        </div>
      </div>
      
      <div className="shops-container">
        {filteredShops.length === 0 ? (
          <div className="no-results">
            No shops found matching your search criteria.
          </div>
        ) : (
          filteredShops.map((shop) => (
            <div key={shop._id} className="shop-card">
              <img 
                src={getImageUrl(shop.imageUrl)} 
                alt={shop.shopName} 
                className="shop-image"
                onError={(e) => {
                  e.target.src = 'https://via.placeholder.com/300x200?text=Error+Loading+Image';
                }}
              />
              <div className="shop-info">
                <h2>{shop.shopName}</h2>
                <p>Owner: {shop.ownerName}</p>
                <p>Location: {shop.location}</p>
                <p>Phone: {shop.phoneNumber}</p>
                <p className={`status ${shop.isOpen ? 'open' : 'closed'}`}>
                  {shop.isOpen ? 'Open' : 'Closed'}
                </p>
                {isAuthenticated && isOwner(shop) && (
                  <div className="shop-actions">
                    <Link to={`/edit-shop/${shop._id}`} className="edit-btn">
                      Edit Shop
                    </Link>
                    <button
                      className="toggle-status-btn"
                      onClick={() => handleToggleStatus(shop._id)}
                    >
                      {shop.isOpen ? 'Mark as Closed' : 'Mark as Open'}
                    </button>
                  </div>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LandingPage;
